import { Box, styled, Button } from '@mui/material';
import { CustomButton } from '../../../styles/sectionStyles';
import { StyleConstants } from '../../../styles/StyleConstants';

const CertificationWrapper = styled(Box)(({ theme }) => ({
	display: "flex",
	justifyContent: "space-between",
	marginBottom: StyleConstants.SPACE_BETWEEN_SECTIONS,
	[theme.breakpoints.down("md")]: {
		flexDirection: "column",
	},
}));

const InfoWrapper = styled(Box)(() => ({
	paddingRight: 15,
	lineHeight: 1.53,
	"& .name": {
		marginBottom: 15,
		fontSize: StyleConstants.SIZE_ITEM_TITLE,
	},
	" &.actions": {
		margin: "12px 0",
	},
}));

const ImageBox = styled(Box)(({ theme }) => ({
	marginRight: 10,
	width: 400,
	minHeight: 250,
	border: "1px solid black",
	"& img": {
		width: 400,
	},
	[theme.breakpoints.down("sm")]: {
		width: 350,
		"& img": {
			width: 350
		},
	},
}));

interface certificate {
	name?: string;
	org?: string;
	issued?: string;
	expiry?: string;
	credentialId?: string;
	credentialUrl?: string;
	url?: string;
}

interface Prop {
	certificate: certificate;
}

export const Certification = ({ certificate }: Prop) => {
	const {
		name = "",
		org = "",
		issued = "",
		expiry = "",
		credentialId = "",
		credentialUrl = "",
		url = ""
	} = certificate;

	return (
		<CertificationWrapper>
			<InfoWrapper>
				<div className="name bold">{name}</div>
				<div>
					<b>Org: </b> {org}
				</div>
				<div>
					<b>Issued: </b> {issued}
				</div>
				{expiry && (
					<div>
						<b>Expiry: </b> {expiry}
					</div>
				)}
				{credentialId && (
					<div>
						<b>Id: </b>{credentialId}
					</div>
				)}
				<div className="actions">
					<CustomButton>
						<a href={credentialUrl} target="_blank" rel="noreferrer">
							Certificate 
						</a>
					</CustomButton>
				</div>
			</InfoWrapper>
			<ImageBox>
				<img src={url} alt="certificate" />
			</ImageBox>
		</CertificationWrapper>
	);
};
