import { styled, Box } from '@mui/material';
import MyImage from '../../../assets/pic.png';
import ExampleRes from '../../../assets/exampleres.pdf';

import {
	CustomButton,
	SectionWrapper,
	TitleWrapper
} from '../../../styles/sectionStyles.tsx';

import aboutInfo from '../../../data/About.json';
import { useEffect } from 'react';

const AboutContainer = styled(Box)(({ theme }) => ({
	display: "flex",
	alignSelf: "center",
	"& img": {
		width: 400,
		[theme.breakpoints.down("sm")]: {
			width: 350,
		},
	},
	[theme.breakpoints.down("md")]: {
		flexDirection: "column",
	},
}));

const DetailsWrapper = styled(Box)(({ theme }) => ({
	fontSize: 16,
	marginLeft: 15,
	"& .actions": {
		margin: "12px 0",
	},
	[theme.breakpoints.down("md")]: {
		margin: "10px 0 0 0",
	},
}));

export const About = () => {
	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	return (
		<SectionWrapper>
			<TitleWrapper>About Me</TitleWrapper>
			<AboutContainer>
				<img src={MyImage} />
				<DetailsWrapper>
					<div>{aboutInfo.about}</div>
					<div className="actions">
						<CustomButton>
							<a href={ExampleRes} rel="noreferrer" target="_blank">View Resume</a>
						</CustomButton>
					</div>
				</DetailsWrapper>
			</AboutContainer>
		</SectionWrapper>
	);
};
